package DTO;

public class Empresa {
	
	private String emp_nombre;
	private String emp_ruc;
	private String emp_actividad;
	private String emp_tamanio;
	private String emp_capital;
	private String emp_ambito;
	private String emp_beneficio;
	private String emp_fjuridico;
	private String emp_produccion;
	private String emp_socios;
	private boolean emp_ce;//ce c:Contriyente e:Especial
	private int emp_subcorp;
	
	public Empresa() {
		// TODO Auto-generated constructor stub
	}

	public String getEmp_nombre() {
		return emp_nombre;
	}

	public void setEmp_nombre(String emp_nombre) {
		this.emp_nombre = emp_nombre;
	}

	public String getEmp_ruc() {
		return emp_ruc;
	}

	public void setEmp_ruc(String emp_ruc) {
		this.emp_ruc = emp_ruc;
	}

	public String getEmp_actividad() {
		return emp_actividad;
	}

	public void setEmp_actividad(String emp_actividad) {
		this.emp_actividad = emp_actividad;
	}

	public String getEmp_tamanio() {
		return emp_tamanio;
	}

	public void setEmp_tamanio(String emp_tamanio) {
		this.emp_tamanio = emp_tamanio;
	}

	public String getEmp_capital() {
		return emp_capital;
	}

	public void setEmp_capital(String emp_capital) {
		this.emp_capital = emp_capital;
	}

	public String getEmp_ambito() {
		return emp_ambito;
	}

	public void setEmp_ambito(String emp_ambito) {
		this.emp_ambito = emp_ambito;
	}

	public String getEmp_beneficio() {
		return emp_beneficio;
	}

	public void setEmp_beneficio(String emp_beneficio) {
		this.emp_beneficio = emp_beneficio;
	}

	public String getEmp_fjuridico() {
		return emp_fjuridico;
	}

	public void setEmp_fjuridico(String emp_fjuridico) {
		this.emp_fjuridico = emp_fjuridico;
	}

	public String getEmp_produccion() {
		return emp_produccion;
	}

	public void setEmp_produccion(String emp_produccion) {
		this.emp_produccion = emp_produccion;
	}

	public String getEmp_socios() {
		return emp_socios;
	}

	public void setEmp_socios(String emp_socios) {
		this.emp_socios = emp_socios;
	}

	public boolean isEmp_ce() {
		return emp_ce;
	}

	public void setEmp_ce(boolean emp_ce) {
		this.emp_ce = emp_ce;
	}

	public int getEmp_subcorp() {
		return emp_subcorp;
	}

	public void setEmp_subcorp(int emp_subcorp) {
		this.emp_subcorp = emp_subcorp;
	}
}
